mapper.select:
SELECT , su.user_id AS "userList.id", su.is_default AS "isDefault" FROM cms_site a
LEFT JOIN cms_site_user su ON su.site_id = a.id
a.del_flag = #{delFlag}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT , su.user_id AS "userList.id", su.is_default AS "isDefault" FROM cms_site a
LEFT JOIN cms_site_user su ON su.site_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_site SET 
	del_flag = #{delFlag}
WHERE id = #{id}

mapper.insert:
INSERT INTO cms_site_user(site_id, user_id)

	SELECT #{id}, #{user.id}

	FROM dual

mapper.update:
UPDATE cms_site_user SET 
	is_Default = '0'
WHERE user_id = #{arg0}

UPDATE cms_site_user SET 
	is_Default = '1'
WHERE site_id = #{arg0}
AND user_id = #{arg1}
