mapper.select:
SELECT

FROM sys_menu a

WHERE a.id = #{id}

mapper.update:
UPDATE sys_menu SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE sys_menu SET 
	sort = #{sort}
WHERE id = #{id}

mapper.insert:
INSERT INTO sys_menu_favorite(
	user_id, 
	menu_id
) VALUES (
	#{userId}, 
	#{id}
)

DELETE FROM sys_menu_favorite
WHERE 
	user_id = #{userId} 
	AND	menu_id = #{id}
